package com.ecommerce.services;

import java.util.ArrayList;
import java.util.Date;
import java.util.List;

import org.springframework.stereotype.Service;

import com.ecommerce.respository.entities.Product;

@Service
public class ProductService {

	
	private  ArrayList<Product > products = new ArrayList<Product>();
	
	public ProductService() {
		Product product1 = new Product(1, "Shirt", 100, 120, 12345, 20, "Cool beautiful shirt", 5, true, new Date(),"data:image/jpeg;base64,");
		Product product2 = new Product(2, "Black Shirt", 150, 200, 12345, 30, "Beautiful shirt", 4, true, new Date(),"data:image/jpeg;base64,");
		Product product3 = new Product(3, "cool Shirt", 20, 15, 12345, 10, "Beautiful shirt", 4, true, new Date(),"data:image/jpeg;base64,");
		Product product4 = new Product(4, "beautiful Shirt", 50, 60, 12345, 5, "Beautiful shirt", 4, true, new Date(),"data:image/jpeg;base64,");
		Product product5 = new Product(5, "red Shirt", 300, 350, 12345, 30, "Beautiful shirt", 4, true, new Date(),"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQgfPhaQqkLHlrvkW-pHZsijQeqOMWrN8fvJ3cauzpey-mBErrT");
		Product product6 = new Product(6, "yellow Shirt", 80, 100, 12345, 12, "Beautiful shirt", 4, true, new Date(),"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ63bYAYjHygaWF-6asqCTOLQSexdB3-G5hGAj_nF0EdL_ePe_FvA");
		products.add(product1);
		products.add(product2);
		products.add(product3);
		products.add(product4);
		products.add(product5);
		products.add(product6);
	}
	public List<Product> findProductByNameContainingIgnoreCase(String text) {
		ArrayList<Product> searchProducts = new ArrayList<Product>();
		for(Product product : products) {
			if(product.getName().equalsIgnoreCase(text)) {
				searchProducts.add(product);
			}
		}
		
		return searchProducts;
	}
	//get product using id
	public Product getProduct(long productId) {
		
		//loop through all products na check if the product id is match to id given by user for serach
		for(Product product : products) {
			
		//if id is math
		if(	product.getId()== productId) {
			//return the product 
				return product;
			}
		}
		
		return null;
	}
	
	//return the list of products
	public ArrayList<Product> getAllProducts() {
		return products;
	}

}
